  return mdx(MDXLayout, _extends({}, layoutProps, props, {
    components: components,
    mdxType: "MDXLayout"
  }), mdx("p", null, "\uD504\uB85C\uC81D\uD2B8\uAC00 \uB05D\uB098\uAC70\uB098 \uC0C8\uB85C\uC6B4 \uD504\uB85C\uC81D\uD2B8\uAC00 \uC2DC\uC791\uD558\uAE30 \uC804\uC5D0\uB294 \"\uB2E4\uC74C\uC5D0\uB294 \uAF2D \uD14C\uC2A4\uD2B8 \uCF54\uB4DC\uB97C \uC791\uC131\uD558\uACA0\uB2E4\"\uB77C\uACE0 \uB2E4\uC9D0\uD558\uC9C0\uB9CC \uB9C8\uC74C\uB300\uB85C \uB418\uC9C0 \uC54A\uC558\uB2E4. \uAC00\uC7A5 \uB9CE\uC774 \uD588\uB358 \uD551\uACC4\uB294 \"\uC2DC\uAC04\uC774 \uBD80\uC871\uD574\uC11C\"\uC600\uB358 \uAC83 \uAC19\uB2E4. \uC0AC\uC2E4\uC740 \uC2DC\uAC04\uC774 \uC788\uC5C8\uC5B4\uB3C4 \uC5B4\uB5BB\uAC8C \uD558\uBA74 \uD14C\uC2A4\uD2B8 \uCF54\uB4DC\uB97C \uC798 \uC791\uC131\uD560 \uC218 \uC788\uB294\uC9C0 \uBAB0\uB790\uAE30 \uB54C\uBB38\uC5D0 \uD14C\uC2A4\uD2B8\uB97C \uC798 \uC9DC\uAE30\uB294 \uD798\uB4E4\uC5C8\uC744 \uAC83 \uAC19\uB2E4. \uADF8\uB7EC\uB358 \uC911\uC5D0 \uD300\uC758 \uB3D9\uB8CC\uBD84\uC5D0\uAC8C \"Effective Unit Testing\"\uC774\uB77C\uB294 \uCC45\uC744 \uCD94\uCC9C\uBC1B\uC544 \uC77D\uAC8C \uB418\uC5C8\uB2E4."), mdx("p", null, "\uC774 \uCC45\uC740 \uC88B\uC740 \uD14C\uC2A4\uD2B8 \uCF54\uB4DC\uB97C \uC791\uC131\uD558\uB294 \uBC29\uBC95\uC744 \uB2E4\uB8E8\uB294 \uCC45\uC774\uB2E4. \uC720\uB2DB \uD14C\uC2A4\uD2B8, E2E \uD14C\uC2A4\uD2B8, \uD1B5\uD569 \uD14C\uC2A4\uD2B8 \uB4F1 \uD14C\uC2A4\uD2B8\uC758 \uC885\uB958\uC5D0\uB3C4 \uC5EC\uB7EC \uAC00\uC9C0\uAC00 \uC788\uB294\uB370, \uADF8\uC911\uC5D0\uC11C \uC720\uB2DB \uD14C\uC2A4\uD2B8\uB97C \uC791\uC131\uD558\uB294 \uBC29\uBC95\uC5D0 \uB300\uD574\uC11C \uB2E4\uB8EC\uB2E4. \uC544\uC26C\uC6B4 \uC810\uC740 \uD14C\uC2A4\uD2B8 \uCF54\uB4DC\uC5D0 \uB300\uD55C \uC608\uC81C\uAC00 Java\uB85C \uC791\uC131\uB41C \uC810\uC774\uB2E4. \uD558\uC9C0\uB9CC \uC774 \uCC45\uC758 \uB0B4\uC6A9\uC740 Java\uC5D0 \uAD6D\uD55C\uB41C \uAC83\uC740 \uC544\uB2C8\uACE0, \uB098\uC640 \uAC19\uC740 \uD504\uB860\uD2B8\uC5D4\uB4DC \uAC1C\uBC1C\uC790\uB4E4\uB3C4 \uCDA9\uBD84\uD788 \uC77D\uC744 \uC218 \uC788\uB294 \uCC45\uC774\uB77C\uACE0 \uC0DD\uAC01\uD55C\uB2E4."), mdx("p", null, "\uC774 \uAE00\uC5D0\uC11C\uB294 \uCC45\uC744 \uC77D\uC73C\uBA74\uC11C \uAC1C\uC778\uC801\uC73C\uB85C \uC0DD\uAC01\uD588\uC744 \uB54C \uAE30\uC5B5\uD558\uACE0 \uC2F6\uC740 \uB0B4\uC6A9\uC744 \uC815\uB9AC\uD588\uB2E4."), mdx("h2", null, "1. \uAE30\uBC18 \uB2E4\uC9C0\uAE30"), mdx("h3", null, "\uC88B\uC740 \uD14C\uC2A4\uD2B8\uC758 \uC774\uC810"), mdx("p", null, "\uD14C\uC2A4\uD2B8 \uCF54\uB4DC\uB97C \uC5B4\uB5A4 \uBAA9\uC801\uC73C\uB85C \uC791\uC131\uD558\uB294\uC9C0\uC5D0 \uB530\uB77C \uC5BB\uC744 \uC218 \uC788\uB294 \uD14C\uC2A4\uD2B8 \uAC00\uCE58\uB294 \uB450 \uAC00\uC9C0\uB85C \uB098\uB25C\uB2E4."), mdx("p", null, "\uBA3C\uC800 \uD14C\uC2A4\uD2B8 \uCF54\uB4DC\uC758 \uBAA9\uC801\uC774 \uD488\uC9C8 \uAC80\uC99D\uC77C \uB54C\uB294 \uD14C\uC2A4\uD2B8 \uCF54\uB4DC\uB97C \uD1B5\uD574 \uD504\uB85C\uADF8\uB7A8\uC758 \uC2E0\uB8B0\uC131\uC744 \uB192\uC774\uB294 \uAC83\uC5D0 \uC9D1\uC911\uD55C\uB2E4. \uC774 \uBAA9\uC801\uC740 \uD504\uB85C\uC81D\uD2B8\uC758 \uD14C\uC2A4\uD2B8 \uCEE4\uBC84\uB9AC\uC9C0\uAC00 \uB0AE\uC744 \uB54C \uC720\uC6A9\uD55C \uBAA9\uD45C\uC774\uB2E4. \uB9CC\uC57D \uD14C\uC2A4\uD2B8 \uCF54\uB4DC\uC758 \uCEE4\uBC84\uB9AC\uC9C0\uAC00 95% \uC815\uB3C4\uB85C \uB192\uC544\uC84C\uC744 \uB54C \uD14C\uC2A4\uD2B8\uB97C \uC791\uC131\uD558\uB294 \uB178\uB825\uC5D0 \uB300\uBE44\uD574 \uC5BB\uC744 \uC218 \uC788\uB294 \uAC00\uCE58\uB294 \uC801\uC740 \uAC83\uCC98\uB7FC \uBCF4\uC77C \uC218 \uC788\uB2E4."), mdx("p", null, "\uC774\uB54C \uD14C\uC2A4\uD2B8 \uCF54\uB4DC\uC758 \uC791\uC131\uC73C\uB85C \uC5BB\uC744 \uC218 \uC788\uB294 \uAC00\uCE58\uB97C \uC124\uACC4\uC758 \uAD00\uC810\uC73C\uB85C \uC804\uD658\uD558\uB294 \uAC83\uC774 \uD544\uC694\uD558\uB2E4. \uC774\uB7EC\uD55C \uAC1C\uBC1C \uBC29\uBC95\uB860\uC740 \uD14C\uC2A4\uD2B8 \uC8FC\uB3C4 \uAC1C\uBC1C(TDD)\uB85C \uB110\uB9AC \uC54C\uB824\uC84C\uB2E4. TDD\uB294 \uAD6C\uD604 \uCF54\uB4DC\uB97C \uC791\uC131\uD558\uAE30 \uC804 \uC2E4\uD328\uD558\uB294 \uD14C\uC2A4\uD2B8\uB97C \uC791\uC131\uD558\uACE0, \uADF8 \uD14C\uC2A4\uD2B8\uB97C \uD1B5\uACFC\uD558\uAE30 \uC704\uD55C \uAD6C\uD604 \uCF54\uB4DC\uB97C \uC791\uC131\uD558\uACE0, \uC791\uC131\uD588\uB358 \uCF54\uB4DC\uB97C \uB9AC\uD329\uD1A0\uB9C1\uD558\uB294 \uACFC\uC815\uC744 \uBC18\uBCF5\uD558\uB294 \uBC29\uBC95\uB860\uC744 \uB9D0\uD55C\uB2E4. \uADF8\uB9AC\uACE0 \uCF54\uB4DC\uB97C \uB9AC\uD329\uD1A0\uB9C1\uD558\uB294 \uACFC\uC815\uC5D0\uC11C \uD504\uB85C\uADF8\uB7A8\uC744 \uC124\uACC4\uD55C\uB2E4. TDD\uB294 \uD14C\uC2A4\uD2B8 \uCF54\uB4DC\uB97C \uD1B5\uD55C \uACB0\uACFC\uBCF4\uB2E4 \uADF8 \uACFC\uC815\uC5D0\uC11C \uC5BB\uC744 \uC218 \uC788\uB294 \uC774\uC810\uC774 \uB9CE\uB2E4."), mdx("p", null, "\uC774 \uCC45\uC5D0\uC11C\uB294 TDD\uC5D0 \uAD00\uD574\uC11C \uC124\uBA85\uD558\uAE30\uBCF4\uB2E4 \"\uD6A8\uC728\uC801\uC778 \uD14C\uC2A4\uD2B8\", \"\uC88B\uC740 \uD14C\uC2A4\uD2B8\"\uC5D0 \uB300\uD574\uC11C \uB354 \uC9D1\uC911\uD55C\uB2E4."), mdx("h3", null, "\uC88B\uC740 \uD14C\uC2A4\uD2B8\uB780?"), mdx("p", null, "\uD14C\uC2A4\uD2B8 \uCF54\uB4DC\uB3C4 \uC18C\uC2A4 \uCF54\uB4DC\uC774\uAE30 \uB54C\uBB38\uC5D0 \uC88B\uC740 \uCF54\uB4DC\uC640 \uB098\uC05C \uCF54\uB4DC\uB85C \uAD6C\uBD84\uD560 \uC218 \uC788\uB2E4. \uC800\uC790\uAC00 \uACBD\uD5D8\uD588\uB358 \"\uC88B\uC740 \uD14C\uC2A4\uD2B8 \uCF54\uB4DC\"\uC758 \uC870\uAC74\uC740 \uB2E4\uC74C\uACFC \uAC19\uB2E4."), mdx("h4", null, "\uAC00\uB3C5\uC131\uC774 \uC88B\uC740 \uCF54\uB4DC\uC778\uAC00?"), mdx("p", null, "\uC77D\uAE30 \uC26C\uC6B4 \uCF54\uB4DC\uAC00 \uC720\uC9C0\uBCF4\uC218\uD558\uAE30 \uC88B\uB2E4\uB294 \uB9D0\uC774 \uC788\uB2E4. \uC77D\uAE30 \uC26C\uC6B4 \uCF54\uB4DC\uB294 \uC77D\uB294 \uC0AC\uB78C\uC774 \uCF54\uB4DC\uB97C \uC774\uD574\uD558\uB294 \uB370 \uD544\uC694\uD55C \uC2DC\uAC04\uC744 \uC904\uC5EC\uC8FC\uBA70, \uB514\uBC84\uAE45\uC744 \uC6A9\uC774\uD558\uAC8C \uD574\uC900\uB2E4. \uAD6C\uD604 \uCF54\uB4DC\uC640 \uB9C8\uCC2C\uAC00\uC9C0\uB85C \uD14C\uC2A4\uD2B8 \uCF54\uB4DC\uB3C4 \uC5EC\uB7EC \uC0AC\uB78C\uC5D0 \uC758\uD574 \uC218\uC815\uB420 \uC218 \uC788\uB294 \uCF54\uB4DC\uC774\uB2E4. \uAC00\uB3C5\uC131\uC774 \uC5C6\uB294 \uCF54\uB4DC\uB294 \uD14C\uC2A4\uD2B8 \uCF54\uB4DC\uB97C \uC774\uD574\uD558\uAE30 \uC5B4\uB835\uAC8C \uD558\uACE0, \uD14C\uC2A4\uD2B8 \uCF54\uB4DC\uB97C \uC218\uC815\uD558\uB294 \uAC83\uC744 \uC5B4\uB835\uAC8C \uD55C\uB2E4."), mdx("h4", null, "\uAD6C\uC870\uD654\uAC00 \uC798 \uB418\uC5C8\uB294\uAC00?"), mdx("p", null, "\uD558\uB098\uC758 \uD30C\uC77C\uC5D0\uC11C \uB108\uBB34 \uB9CE\uC740 \uD14C\uC2A4\uD2B8 \uCF54\uB4DC\uAC00 \uC788\uB2E4\uBA74 \uD14C\uC2A4\uD2B8 \uCF54\uB4DC\uC758 \uC804\uCCB4\uC801\uC778 \uAD6C\uC870\uB97C \uD30C\uC545\uD558\uAE30 \uD798\uB4E4\uB2E4. \uC774 \uACBD\uC6B0 \uAC00\uB2A5\uD558\uBA74 \uC791\uC740 \uB2E8\uC704\uB85C \uB098\uB220\uC57C \uD558\uC9C0\uB9CC \uBB34\uC791\uC704\uB85C \uB098\uB204\uB294 \uAC83\uC740 \uC624\uD788\uB824 \uC5EC\uB7EC \uD30C\uC77C\uC744 \uC624\uAC00\uBA74\uC11C \uD14C\uC2A4\uD2B8 \uCF54\uB4DC\uB97C \uC77D\uC5B4\uC57C \uD558\uB294 \uBD88\uD3B8\uD568\uC774 \uC788\uB2E4. \uAC04\uB2E8\uD55C \uBC29\uBC95\uC740 \uCD94\uC0C1\uD654\uB41C \uAC1C\uB150\uC744 \uB2E8\uC704\uB85C \uD14C\uC2A4\uD2B8 \uCF54\uB4DC\uB97C \uCABC\uAC1C\uB294 \uBC29\uBC95\uC774 \uC788\uB2E4."), mdx("p", null, "\uC798 \uB098\uB204\uC5B4\uC9C4 \uD14C\uC2A4\uD2B8 \uCF54\uB4DC\uB294 \uAD6C\uD604 \uCF54\uB4DC\uB97C \uD30C\uC545\uD558\uB294 \uAC83\uC5D0\uB3C4 \uB3C4\uC6C0\uC774 \uB41C\uB2E4. \uAD6C\uD604 \uCF54\uB4DC\uAC00 \uC5B4\uB5BB\uAC8C \uC2E4\uD589\uB418\uB294\uC9C0 \uD30C\uC545\uD558\uAE30 \uC704\uD574\uC11C\uB294 \uD14C\uC2A4\uD2B8 \uCF54\uB4DC\uB97C \uC77D\uB294 \uAC83\uC774 \uB3C4\uC6C0\uC774 \uB41C\uB2E4. \uADF8\uB9AC\uACE0 \uC218\uC815\uC774 \uD544\uC694\uD55C \uAE30\uB2A5\uC774 \uC788\uB2E4\uBA74, \uC5B4\uB5A4 \uD14C\uC2A4\uD2B8 \uCF54\uB4DC\uB97C \uBD10\uC57C \uD560 \uC9C0 \uD30C\uC545\uD558\uAE30\uB3C4 \uC26C\uC6CC\uC9C4\uB2E4."), mdx("h4", null, "\uBB34\uC5C7\uC744 \uAC80\uC0AC\uD558\uB294\uAC00?"), mdx("p", null, "\uD14C\uC2A4\uD2B8\uAC00 \uBB34\uC5C7\uC744 \uAC80\uC0AC